export class GameRoomComponent {
  constructor(route, router, gameApiService, socketService) {
    this.route = route;
    this.router = router;
    this.gameApiService = gameApiService;
    this.socketService = socketService;
    this.roomCode = '';
    this.roomName = 'Cosmic Game Room';
    this.gamePhase = 'waiting';
    // Real game data
    this.currentRoom = null;
    this.players = [];
    // Game state
    this.myCards = [];
    this.opponentCards = 0;
    this.discardPile = [];
    this.deckCount = 0;
    // Subscriptions
    this.subscriptions = [];
    this.playerSlots = [];
    this.currentPlayerId = '';
    this.currentPlayerName = '';
    this.hostId = '';
    this.isHost = false;
    this.isPlayerReady = false;
    this.canStartGame = false;
    this.myPlayerId = ''; // Track which player this client represents  
    this.myPlayerName = ''; // Track this client's player name
    // Player color themes for visual differentiation
    this.playerColors = [{
      frame: 'player-frame-red',
      name: 'player-name-red'
    }, {
      frame: 'player-frame-blue',
      name: 'player-name-blue'
    }, {
      frame: 'player-frame-green',
      name: 'player-name-green'
    }, {
      frame: 'player-frame-purple',
      name: 'player-name-purple'
    }, {
      frame: 'player-frame-orange',
      name: 'player-name-orange'
    }, {
      frame: 'player-frame-pink',
      name: 'player-name-pink'
    }, {
      frame: 'player-frame-yellow',
      name: 'player-name-yellow'
    }, {
      frame: 'player-frame-cyan',
      name: 'player-name-cyan'
    }];
    // Room settings
    this.maxPlayers = 6;
    this.gameVariant = 'classic';
    this.turnTimeLimit = 30;
    this.pointLimit = 100;
    // Chat
    this.chatMessages = [];
    this.chatInput = '';
  }
  ngOnInit() {
    this.roomCode = this.route.snapshot.params['code'];
    console.log('Joining room:', this.roomCode);
    // Get player name from localStorage (should be set when joining/creating room)
    this.myPlayerName = localStorage.getItem('currentPlayerName') || 'Player';
    // Connect to WebSocket
    this.socketService.connect();
    // Subscribe to real-time updates
    this.setupSocketSubscriptions();
    // Join the room via socket (important for session tracking)
    this.socketService.joinRoom(this.roomCode, this.myPlayerName);
    // Load room data
    this.loadRoomData();
  }
  ngOnDestroy() {
    // Cleanup subscriptions
    this.subscriptions.forEach(sub => sub.unsubscribe());
    // Leave room and disconnect socket
    if (this.roomCode) {
      this.socketService.leaveRoom(this.roomCode);
    }
    this.socketService.disconnect();
  }
  setupSocketSubscriptions() {
    // Room updates
    this.subscriptions.push(this.socketService.roomUpdated$.subscribe(room => {
      if (room) {
        this.updateRoomData(room);
      }
    }));
    // Game updates
    this.subscriptions.push(this.socketService.gameUpdated$.subscribe(gameState => {
      if (gameState && this.currentRoom) {
        this.currentRoom.gameState = gameState;
        this.gamePhase = gameState.phase;
      }
    }));
    // Player joined
    this.subscriptions.push(this.socketService.playerJoined$.subscribe(player => {
      if (player && this.currentRoom) {
        // Player already added via room update, just show notification
        this.addChatMessage('System', `${player.name} joined the room`);
      }
    }));
    // Player left
    this.subscriptions.push(this.socketService.playerLeft$.subscribe(playerId => {
      if (playerId && this.currentRoom) {
        const player = this.currentRoom.players.find(p => p.id === playerId);
        if (player) {
          this.addChatMessage('System', `${player.name} left the room`);
        }
      }
    }));
    // Chat messages
    this.subscriptions.push(this.socketService.chatReceived$.subscribe(chat => {
      if (chat) {
        this.addChatMessage(chat.playerName, chat.message);
      }
    }));
    // Errors
    this.subscriptions.push(this.socketService.error$.subscribe(error => {
      if (error) {
        console.error('Socket error:', error);
        this.addChatMessage('System', `Error: ${error}`);
      }
    }));
    // Game started
    this.subscriptions.push(this.socketService.gameStarted$.subscribe(started => {
      if (started) {
        this.gamePhase = 'playing';
        this.addChatMessage('System', 'Game started!');
      }
    }));
  }
  loadRoomData() {
    this.gameApiService.getRoom(this.roomCode).subscribe({
      next: response => {
        if (response.success && response.data) {
          this.updateRoomData(response.data);
        } else {
          console.error('Failed to load room:', response.error);
          this.router.navigate(['/']);
        }
      },
      error: error => {
        console.error('Error loading room:', error);
        this.router.navigate(['/']);
      }
    });
  }
  updateRoomData(room) {
    this.currentRoom = room;
    this.roomName = room.name;
    this.players = room.players;
    this.hostId = room.hostId;
    this.gamePhase = room.gameState.phase;
    this.maxPlayers = room.settings.maxPlayers;
    this.gameVariant = room.settings.gameVariant;
    this.turnTimeLimit = room.settings.turnTimeLimit;
    this.pointLimit = room.settings.pointLimit;
    // Find current player by name
    const myPlayer = this.players.find(p => p.name === this.myPlayerName);
    if (myPlayer) {
      this.myPlayerId = myPlayer.id;
      this.isPlayerReady = myPlayer.isReady;
    }
    // Update player slots
    this.playerSlots = Array(this.maxPlayers).fill(null).map((_, i) => ({
      index: i,
      player: i < this.players.length ? this.players[i] : null
    }));
    // Determine if current user is host
    this.isHost = this.hostId === this.myPlayerId;
    // Check if can start game
    this.canStartGame = this.isHost && this.players.length >= 2 && this.players.every(p => p.isReady) && this.gamePhase === 'waiting';
    // Update current player info for game state
    if (room.gameState.currentPlayer) {
      const currentPlayer = this.players.find(p => p.id === room.gameState.currentPlayer);
      this.currentPlayerName = currentPlayer?.name || '';
    }
    // Update game data for display
    if (this.gamePhase === 'playing') {
      // Get my cards
      const myPlayer = this.players.find(p => p.id === this.myPlayerId);
      this.myCards = myPlayer?.cards || [];
      // Count opponent cards (for display purposes)
      const opponents = this.players.filter(p => p.id !== this.myPlayerId);
      this.opponentCards = opponents.length > 0 ? opponents[0].cards.length : 0;
      // Game state data
      this.discardPile = room.gameState.discardPile || [];
      this.deckCount = room.gameState.deck?.length || 0;
    }
    console.log('Room updated. My player:', {
      id: this.myPlayerId,
      name: this.myPlayerName,
      ready: this.isPlayerReady
    });
  }
  addChatMessage(playerName, text) {
    this.chatMessages.push({
      playerName,
      text,
      timestamp: new Date()
    });
  }
  /**
   * Get color theme for a player based on their index
   */
  getPlayerColorTheme(playerIndex) {
    return this.playerColors[playerIndex % this.playerColors.length];
  }
  /**
   * Get frame class for a player slot
   */
  getPlayerFrameClass(slot) {
    if (!slot.player) return '';
    const playerIndex = this.players.findIndex(p => p.id === slot.player.id);
    const theme = this.getPlayerColorTheme(playerIndex);
    return theme.frame;
  }
  /**
   * Get name class for a player slot
   */
  getPlayerNameClass(slot) {
    if (!slot.player) return 'text-casino-gold';
    const playerIndex = this.players.findIndex(p => p.id === slot.player.id);
    const theme = this.getPlayerColorTheme(playerIndex);
    return theme.name;
  }
  toggleReady() {
    // Don't optimistically update - wait for server confirmation
    this.socketService.togglePlayerReady(this.roomCode);
    console.log('Requesting ready status toggle...');
  }
  startGame() {
    if (!this.canStartGame) return;
    this.socketService.startGame(this.roomCode);
    console.log('Starting game...');
  }
  sendChatMessage() {
    if (!this.chatInput.trim()) return;
    this.socketService.sendChatMessage(this.roomCode, this.chatInput.trim());
    this.chatInput = '';
  }
  leaveRoom() {
    if (confirm('Are you sure you want to leave the room?')) {
      this.socketService.leaveRoom(this.roomCode);
      this.router.navigate(['/']);
    }
  }
  /**
   * Get display string for a card
   */
  getCardDisplay(card) {
    if (!card) return '';
    // Card suit symbols
    const suitSymbols = {
      'hearts': '‚ô•',
      'diamonds': '‚ô¶',
      'clubs': '‚ô£',
      'spades': '‚ô†'
    };
    const suit = suitSymbols[card.suit] || card.suit;
    return `${card.rank}${suit}`;
  }
  getCardRank(card) {
    return card?.rank || '';
  }
  getCardSuit(card) {
    const suitSymbols = {
      'hearts': '‚ô•',
      'diamonds': '‚ô¶',
      'clubs': '‚ô£',
      'spades': '‚ô†'
    };
    return suitSymbols[card?.suit] || card?.suit || '';
  }
  getCardColor(card) {
    if (!card) return 'black';
    return card.suit === 'hearts' || card.suit === 'diamonds' ? 'red' : 'black';
  }
